import axios from "axios";
import { selectAccessToken } from "../reduser/selectors.js";

let store;

export const setStore = (s) => {
  store = s;
};

export const instans = axios.create({
    withCredentials: true,
    // baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    // headers: {'API-KEY': '326b0522-c61e-4d4e-a7df-aae77c6de2db'}
    baseURL: 'http://127.0.0.1:8000/api/'
})


export const configureAxiosInterceptors = () => {
  // Добавляем interceptor для автоматического добавления токена в заголовок запроса
  instans.interceptors.request.use(
    (config) => {
      const token = selectAccessToken(store.getState()); // Получаем токен из localStorage
      // const refreshToken = localStorage.getItem('refreshToken'); // Получаем токен из localStorage
      console.log(
        "Access token retrieved from localStorage:",
        `Bearer ${token}`
      );
      // console.log('refreshToken', `Bearer ${refreshToken}`);
      if (token) {
        config.headers["Authorization"] = `Bearer ${token}`;
      }

      return config;
    },
    (error) => {
      return Promise.reject(error);
    }
  );
};

export const dataAPI = {

  getStudent() {
    return instans.get(`students/`)
  },

  getCurses() {
    return instans.get(`curses/`)
  }


}


const authAPI = {

  authMe(refreshToken) {
    let result = instans.post(`token/refresh/`, { refresh: refreshToken });
    return result;
  },

  getAythUser() {
    return instans.get.apply(`user/`)
  },

  registerUser(fullName, login, email, password, repidPassword) {
    let result = instans.post(`register/`, {
      full_name: fullName,
      username: login,
      email,
      password,
      repidPassword,
    });
    return result;
  },

  loginUser(username, password) {
    // console.log(email, password)
    let result = instans.post(
      "token/obtain/",
      { username, password },
      { headers: { "Content-Type": "application/json" } }
    );
    return result;
  },

  logout() {
    return instans.delete("auth/logout/");
  },

  refreshToken(refreshToken) {
    let result = instans.post("token/refresh/", { refresh: refreshToken });
    return result;
  },
}; 





export default authAPI
